import { useState } from "react";
import { useForm } from "react-hook-form";
import { Employee, Property } from "../Types";
import {
  ButtonContainer,
  CancelButton,
  EmailInput,
  Form,
  Input,
  InputContainer,
  SubmitButton,
} from "./Styles";

const EditForm = (props: {
  currentEmployee: Employee;
  expanded: boolean;
  toggle: () => void;
  updateEmployee: (id: number, data: Employee) => Promise<void>;
  id: number;
}) => {
  const [employee, setEmployee] = useState<Employee>(props.currentEmployee);
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm({
    defaultValues: {
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
      role: employee.role,
      age: employee.age,
    },
  });

  // onClick={() => {
  //   props.toggle();
  //   props.updateEmployee(props.id, employee);

  return (
    <Form expanded={props.expanded}>
      <form>
        {" "}
        <InputContainer>
          <Input
            id="firstName"
            {...register("firstName", { required: "This is required" })}
          />
          <Input {...register("lastName", { required: "This is required" })} />
          <EmailInput></EmailInput>
          <Input></Input>
          <Input
            style={{ width: "100px" }}
            type="number"
            min="0"
            id="age"
          ></Input>
        </InputContainer>
        <ButtonContainer>
          <SubmitButton type="submit" value="Confirm" />
          <CancelButton onClick={props.toggle}>Cancel</CancelButton>
        </ButtonContainer>
      </form>
    </Form>
  );
};

export default EditForm;
