import { useState } from "react";
import { useForm } from "react-hook-form";
import { BadInput, Employee, Property } from "../Types";
import {
  ButtonContainer,
  ErrorMessage,
  FadedBackround,
  FormContainer,
  Header,
  Input,
  InputContainer,
  Label,
  MailInput,
  Name,
  Window,
  SubmitButton,
  CancelButton,
} from "./Styles";

const defaultValue: Employee = {
  firstName: "",
  lastName: "",
  email: "",
  role: "",
  age: 0,
};

const badInputDefault: BadInput = {
  firstNameBad: false,
  lastNameBad: false,
  emailBad: false,
  roleBad: false,
  ageBad: false,
};

const AddModal = (props: {
  closeModal: () => void;
  addEmployee: (employee: Employee) => Promise<void>;
}) => {
  const [employee, setEmployee] = useState<Employee>(defaultValue);
  const [badInput, setBadInput] = useState<BadInput>(badInputDefault);
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    defaultValues: {
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
      role: employee.role,
      age: employee.age,
    },
  });

  return (
    <>
      <Window>
        <Header>New Employee</Header>
        <FormContainer
          onSubmit={handleSubmit((data) => {
            setEmployee(data);
            props.addEmployee(employee);
            console.log(data);
          })}
        >
          <Name>
            <InputContainer>
              {/* //<Label>First Name</Label> */}
              <Input
                id="firstName"
                {...register("firstName", { required: "This is required" })}
                placeholder="First Name"
              />
              <ErrorMessage>{errors.firstName?.message}</ErrorMessage>
            </InputContainer>
            <InputContainer>
              {/* <Label>Last Name</Label> */}
              <Input
                id="lastName"
                {...register("lastName", { required: "This is required" })}
                placeholder="Last Name"
                onChange={(e) => {
                  handleChange(e.target.value, Property.lastName);
                }}
              />
              <ErrorMessage>{errors.lastName?.message}</ErrorMessage>
            </InputContainer>
          </Name>
          <Name>
            <InputContainer>
              {/* <Label>Role</Label> */}
              <Input
                {...register("role", { required: "Role is required" })}
                placeholder="Role"
                onChange={(e) => {
                  handleChange(e.target.value, Property.role);
                }}
              />
              <ErrorMessage>{errors.role?.message}</ErrorMessage>
            </InputContainer>
            <Label>Age:</Label>
            <InputContainer style={{ width: "100px" }}>
              <Input
                type="number"
                min="0"
                {...register("age", { required: "This is required" })}
                onChange={(e) => {
                  handleChange(e.target.value, Property.age);
                }}
              />
              <ErrorMessage>{errors.age?.message}</ErrorMessage>
            </InputContainer>
          </Name>
          <InputContainer>
            {/* <Label>Email</Label> */}
            <MailInput
              id="email"
              {...register("email", { required: "This is required" })}
              placeholder="Email"
              onChange={(e) => {
                handleChange(e.target.value, Property.email);
              }}
              badEmail={badInput.emailBad}
            />
            <ErrorMessage>{errors.email?.message}</ErrorMessage>
          </InputContainer>
          <ButtonContainer>
            <SubmitButton type="submit" />
            <CancelButton onClick={props.closeModal}>Cancel</CancelButton>
          </ButtonContainer>
        </FormContainer>
      </Window>
      <FadedBackround />
    </>
  );
};

export default AddModal;
