import { useState } from "react";
import { useForm } from "react-hook-form";
import { MyButton, SubmitButton } from "../EditForm/Styles";
import { BadInput, Employee, Property } from "../Types";
import {
  ButtonContainer,
  FadedBackround,
  FormContainer,
  Header,
  Input,
  InputContainer,
  Label,
  MailInput,
  Name,
  Window,
} from "./Styles";

const defaultValue: Employee = {
  firstName: "",
  lastName: "",
  email: "",
  role: "",
  age: 0,
};

const badInputDefault: BadInput = {
  firstNameBad: false,
  lastNameBad: false,
  emailBad: false,
  roleBad: false,
  ageBad: false,
};

const AddModal = (props: {
  closeModal: () => void;
  addEmployee: (employee: Employee) => Promise<void>;
}) => {
  const [employee, setEmployee] = useState<Employee>(defaultValue);
  const [badInput, setBadInput] = useState<BadInput>(badInputDefault);
  const { register } = useForm();

  const handleChange = (value: string, property: Property) => {
    switch (property) {
      case Property.firstName:
        setEmployee({ ...employee, firstName: value });
        break;
      case Property.lastName:
        setEmployee({ ...employee, lastName: value });
        break;
      case Property.email:
        setEmployee({ ...employee, email: value });
        break;
      case Property.role:
        setEmployee({ ...employee, role: value });
        break;
      case Property.age:
        setEmployee({ ...employee, age: Number(value) });
        break;
    }
  };

  const confirm = () => {
    props.addEmployee(employee);
  };

  // onClick={confirm}

  return (
    <>
      <Window>
        <Header>New Employee</Header>
        <FormContainer>
          <Name>
            <InputContainer>
              <Label>First Name</Label>
              <Input
                id="firstName"
                {...register("firstName")}
                placeholder="Kenny"
                onChange={(e) => {
                  handleChange(e.target.value, Property.firstName);
                }}
              />
            </InputContainer>
            <InputContainer>
              <Label>Last Name</Label>
              <Input
                id="lastName"
                {...register("lastName")}
                placeholder="McCormick"
                onChange={(e) => {
                  handleChange(e.target.value, Property.lastName);
                }}
              />
            </InputContainer>
          </Name>
          <Name>
            <InputContainer>
              <Label>Role</Label>
              <Input
                {...register("role")}
                placeholder="4th Grader"
                onChange={(e) => {
                  handleChange(e.target.value, Property.role);
                }}
              />
            </InputContainer>
            <InputContainer style={{ width: "100px" }}>
              <Label>Age</Label>
              <Input
                type="number"
                min="0"
                {...register("age")}
                placeholder="9"
                onChange={(e) => {
                  handleChange(e.target.value, Property.age);
                }}
              />
            </InputContainer>
          </Name>
          <InputContainer>
            <Label>Email</Label>
            <MailInput
              id="email"
              {...register("email")}
              placeholder="mysterion@yahoo.com"
              onChange={(e) => {
                handleChange(e.target.value, Property.email);
              }}
              badEmail={badInput.emailBad}
            />
          </InputContainer>
          <ButtonContainer>
            <SubmitButton type="submit" />
            <MyButton onClick={props.closeModal}>Cancel</MyButton>
          </ButtonContainer>
        </FormContainer>
      </Window>
      <FadedBackround />
    </>
  );
};

export default AddModal;
