import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import { useQuery } from "react-query";

import {
  TopBar,
  Container,
  Table,
  TopRow,
  Cell,
  EmailCell,
  SmallCell,
  AddButton,
  Background,
} from "./Components/Styles";
import { Employee, EmployeeRow } from "./Components/Types";
import Row from "./Components/Row";
import AddModal from "./Components/AddModal/AddModal";

function App() {
  const [employees, setEmployees] = useState<EmployeeRow[]>([]);
  const [add, toggleAdd] = useState<boolean>(false);

  const {} = useQuery;

  const getEmployees = async () => {
    const result = await axios.get("http://localhost:8080/employees/");
    setEmployees(result.data);
  };

  const addEmployee = async (employee: Employee) => {
    const response = await axios.post(
      "http://localhost:8080/employees/",
      employee
    );
    if (response.status === 200) {
      console.log("Success");
    } else {
      console.log(response.statusText);
    }
  };

  useEffect(() => getEmployees(), []);

  const closeModal = () => {
    toggleAdd(false);
  };

  return (
    <div className="App">
      {add && <AddModal closeModal={closeModal} />}
      <Background>
        <TopBar>Employee Manager</TopBar>
        <Container>
          <AddButton onClick={() => toggleAdd(true)}>Add Employee</AddButton>
          <Table>
            <TopRow>
              <Cell>First Name</Cell>
              <Cell>Last Name</Cell>
              <EmailCell>Email</EmailCell>
              <Cell>Role</Cell>
              <SmallCell>Age</SmallCell>
              <SmallCell></SmallCell>
            </TopRow>
            {employees.map((employee) => (
              <Row employee={employee} />
            ))}
          </Table>
        </Container>
      </Background>
    </div>
  );
}

export default App;
