import { useState } from "react";
import EditForm from "./EditForm/EditForm";
import { StyledRow, Cell, RowContainer } from "./Styles";
import { Employee } from "./Types";

const Row = (props: { employee: Employee }) => {
  const { employee } = props;

  const [expanded, toggleExpanded] = useState<boolean>(false);

  return (
    <RowContainer onClick={() => toggleExpanded(true)}>
      <StyledRow>
        <Cell>{employee.firstName}</Cell>
        <Cell>{employee.lastName}</Cell>
        <Cell>{employee.email}</Cell>
        <Cell>{employee.role}</Cell>
        <Cell>{employee.age}</Cell>
      </StyledRow>
      <EditForm currentEmployee={employee} expanded={expanded} />
    </RowContainer>
  );
};

export default Row;
