import styled from "styled-components";

export const Window = styled.div`
  box-sizing: border-box;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 11;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  border: transparent;
  background-color: white;
  border-radius: 12px;
  box-shadow: -4px 5px 5px 5px #00000070;
`;

export const Header = styled.div`
  background-color: #2a2537;
  width: 100%;
  padding: 10px;
  color: white;
  font-weight: bold;
  font-size: 20px;
  border-radius: 8px 8px 0 0;
`;

export const FormContainer = styled.form`
  background-color: #2a2537;
  box-sizing: border-box;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  padding: 10px;
  border-radius: 0 0 8px 8px;
`;

export const InputContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 5px 10px 5px 10px;
`;

export const Input = styled.input`
  display: block;
  padding: 10px 12px;
  border-radius: 8px;
  border: none;
  outline: none;
  font-size: 14px;
`;

export const MailInput = styled.input<{ badEmail: boolean }>`
  padding: 8px;
  border-radius: 8px;
  border: ${(p) => (p.badEmail ? "2px solid #ec0202" : "2px solid #00000045")};
  outline: none;
`;

export const Name = styled.div`
  display: flex;
  width: 100%;
`;

export const Label = styled.label`
  margin-bottom: 3px;
  align-self: center;
  justify-self: flex-start;
  padding-left: 12px;
  color: white;
  font-weight: bold;
`;

export const FadedBackround = styled.div`
  box-sizing: border-box;
  position: absolute;
  width: 100vw;
  height: 100vh;
  background-color: black;
  opacity: 0.5;
  z-index: 10;
`;

export const ButtonContainer = styled.div`
  display: flex;
  padding: 10px 10px;
`;

export const ErrorMessage = styled.p`
  margin: 0;
  font-size: 14px;
  text-align: start;
  padding: 0 0 4px 12px;
  color: red;
`;
