import { useState } from "react";
import EditForm from "./EditForm/EditForm";
import {
  StyledRow,
  Cell,
  RowContainer,
  EmailCell,
  SmallCell,
  SvgContainer,
} from "./Styles";
import { Employee } from "./Types";
import { ReactComponent as Pen } from "../images/pen-solid.svg";
import { ReactComponent as Delete } from "../images/trash-solid.svg";

const Row = (props: { employee: Employee }) => {
  const { employee } = props;

  const [expanded, toggleExpanded] = useState<boolean>(false);

  const toggleEdit = () => {
    toggleExpanded(false);
  };

  return (
    <RowContainer>
      <StyledRow onClick={() => toggleExpanded(true)}>
        <Cell>{employee.firstName}</Cell>
        <Cell>{employee.lastName}</Cell>
        <EmailCell>{employee.email}</EmailCell>
        <SmallCell>{employee.role}</SmallCell>
        <SmallCell>{employee.age}</SmallCell>
        <SmallCell>
          <SvgContainer>
            <Pen width="1rem" style={{ paddingRight: "3px", color: "blue" }} />
            <Delete width="1rem" style={{ paddingLeft: "3px" }} />
          </SvgContainer>
        </SmallCell>
      </StyledRow>
      <EditForm
        currentEmployee={employee}
        expanded={expanded}
        toggle={toggleEdit}
      />
    </RowContainer>
  );
};

export default Row;
