import { useState } from "react";
import { useForm } from "react-hook-form";
import { BadInput, Employee, Property } from "../Types";
import {
  ButtonContainer,
  ErrorMessage,
  FadedBackround,
  FormContainer,
  Header,
  Input,
  InputContainer,
  Label,
  MailInput,
  Name,
  Window,
  SubmitButton,
  CancelButton,
} from "./Styles";

const defaultValue: Employee = {
  firstName: "",
  lastName: "",
  email: "",
  role: "",
  age: 0,
};

const AddModal = (props: {
  closeModal: () => void;
  addEmployee: (employee: Employee) => Promise<void>;
}) => {
  const [employee, setEmployee] = useState<Employee>(defaultValue);
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    defaultValues: {
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
      role: employee.role,
      age: employee.age,
    },
  });

  const setValues = (data: Employee) => {};

  return (
    <>
      <Window>
        <Header>New Employee</Header>
        <FormContainer
          onSubmit={handleSubmit((data) => {
            setEmployee({
              ...employee,
              firstName: data.firstName,
              lastName: data.lastName,
              email: data.email,
              role: data.role,
              age: data.age,
            });
            console.log(employee);
            props.addEmployee(employee);
            props.closeModal();
            console.log(data);
          })}
        >
          <Name>
            <InputContainer>
              <Input
                id="firstName"
                {...register("firstName", { required: "This is required" })}
                placeholder="First Name"
              />
              <ErrorMessage>{errors.firstName?.message}</ErrorMessage>
            </InputContainer>
            <InputContainer>
              <Input
                id="lastName"
                {...register("lastName", { required: "This is required" })}
                placeholder="Last Name"
              />
              <ErrorMessage>{errors.lastName?.message}</ErrorMessage>
            </InputContainer>
          </Name>
          <Name>
            <InputContainer>
              <Input
                {...register("role", { required: "Role is required" })}
                placeholder="Role"
              />
              <ErrorMessage>{errors.role?.message}</ErrorMessage>
            </InputContainer>
            <Label>Age:</Label>
            <InputContainer style={{ width: "100px" }}>
              <Input
                type="number"
                min="0"
                {...register("age", {
                  required: "This is required",
                  valueAsNumber: true,
                })}
              />
              <ErrorMessage>{errors.age?.message}</ErrorMessage>
            </InputContainer>
          </Name>
          <InputContainer>
            <MailInput
              id="email"
              {...register("email", { required: "This is required" })}
              placeholder="Email"
            />
            <ErrorMessage>{errors.email?.message}</ErrorMessage>
          </InputContainer>
          <ButtonContainer>
            <SubmitButton type="submit" value="Confirm" />
            <CancelButton onClick={props.closeModal}>Cancel</CancelButton>
          </ButtonContainer>
        </FormContainer>
      </Window>
      <FadedBackround />
    </>
  );
};

export default AddModal;
